import React from 'react';
import { useState, useEffect, useCallback } from 'react';
import StarWars from '../src/assets/StarWarsPoster.png'
import Logo from '../src/assets/logo.svg'
import Film from '../src/assets/film.svg'
import MoviesList from './components/MoviesList';
import classes from './App.module.css';
import AddMovie from './components/AddMovie';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);


  const FetchMoviesHandler = useCallback(async() =>  {
    setIsLoading(true);
    setError(null)
    try {
      const response = await fetch('https://react-swapi-project-default-rtdb.firebaseio.com/movies.json');
        if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const loadedMovies = [];

      for (const key in data) {
        loadedMovies.push({
          id: key,
          title: data[key].title,
          openingText: data[key].openingText,
          releaseDate: data[key].releaseDate,
        });
      }

      setMovies(loadedMovies);

      // const transformedData = data.map((movieData) => {
      //   return {
      //     id: movieData.episode_id,
      //     title: movieData.title,
      //     openingText: movieData.opening_crawl,
      //     releaseDate: movieData.release_date,
      //   };
      // });

      // setMovies(transformedData);

    } catch (error) {
      // Handle error if the fetch or JSON parsing fails
      setError(error.message)
    }
    setIsLoading(false);
  },[]);

  useEffect(() => {
  FetchMoviesHandler();
 },[FetchMoviesHandler])

 async function addMovieHandler(movie) {
    const response = await fetch('https://react-swapi-project-default-rtdb.firebaseio.com/movies.json', {
      method: 'POST',
      body: JSON.stringify(movie),
      headers: {
        'Content-Type': 'application/json'
      }
    });

  const data = await response.json();
  console.log(data);

  }

  return (
    <React.Fragment>
      <section>
        <header>
          <div className={classes.navbar}>
            <img src={Logo} alt=''></img>
             <a href="/"><svg stroke="currentColor" fill="currentColor" stroke-width="0" viewBox="0 0 24 24" height="20" width="20" xmlns="http://www.w3.org/2000/svg"><path fill="none" d="M0 0h24v24H0z"></path><circle cx="10" cy="8" r="4"></circle><path d="M10.35 14.01C7.62 13.91 2 15.27 2 18v2h9.54c-2.47-2.76-1.23-5.89-1.19-5.99zM19.43 18.02c.36-.59.57-1.28.57-2.02 0-2.21-1.79-4-4-4s-4 1.79-4 4 1.79 4 4 4c.74 0 1.43-.22 2.02-.57L20.59 22 22 20.59l-2.57-2.57zM16 18c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2z"></path></svg>Personagens</a>
          </div>
          <div className={classes['main-image']}>
         <img src={StarWars} alt='Star wars logo'/>
         <div>
          <img src={Film} alt="film-icom"></img>
         <a href="src/components/MoviesList.js">Film</a>

         </div>
         </div>
        </header>
        <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
        <button onClick={FetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section className={classes['list-section']} >
       {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
       {!isLoading && movies.length === 0 && !error && <p>Found no movies</p>}
       {!isLoading && error && <p>{error}</p>}
       {isLoading && <p>Loading...</p> }
      </section>
    </React.Fragment>
  );
}

export default App;
